con:configuration.scriptText:
import groovy.json.JsonSlurper;
import java.io.File;
import java.util.regex.Pattern;
import java.util.regex.Matcher;

def jsonPath = context.testCase.testSuite.getPropertyValue("jsonPath");
def currentStep = context.testCase.getTestStepByName("Listar Clientes");
def dataRegex = /^[1-9][0-9][0-9][0-9][-](0[1-9]|1[012])[-](0[1-9]|[12][0-9]|3[01])$/;
def sexoRegex = /^(F|M)$/;

def originalJson = new JsonSlurper().parse(new File(jsonPath));
def responseJson = new JsonSlurper().parseText( context.expand('${${currentStep.name}#response}'));
assert originalJson == responseJson;
for(int i = 0; i < responseJson.values().size(); i++) {
	def cliente = responseJson.values()[i];
	Matcher matcher = cliente.nascimento =~ dataRegex;
	assert matcher.asBoolean();
	matcher = cliente.sexo =~ sexoRegex;
	assert matcher.asBoolean();
	assert cliente.nome.length() > 10 && cliente.nome.length() <= 100
}

