con:configuration.scriptText:
import groovy.json.JsonSlurper;
import java.io.File;

def jsonPath = context.testCase.testSuite.getPropertyValue("vendasJsonPath");
def originalJson = new JsonSlurper().parse(new File(jsonPath));
def responseJson = new JsonSlurper().parseText( context.expand('${${currentStep.name}#response}'));
assert originalJson == responseJson;

for(int i = 0; i < responseJson.values().size(); i++) {
	def soma = 0;
	def venda = responseJson.values()[i];
	venda.produtos.keySet().each {
		def conn = new URL("https://base2-soapui.firebaseio.com/produtos/" + it +".json?auth="
		+context.testCase.getTestStepAt(0).getPropertyValue("token")).openConnection() as HttpURLConnection;

		conn.setRequestProperty( 'User-Agent', 'groovy-2.4.4' )
		conn.setRequestProperty( 'Accept', 'application/json' )

		if ( conn.responseCode == 200 ) {
		    // get the JSON response
		    def json = conn.inputStream.withCloseable { inStream ->
		        new JsonSlurper().parse( inStream as InputStream )
		    }
		    soma += json.valor;
		}
		else {
			assert false;
		}
	}

	def valorVenda = 0;
	if(venda.desconto > 0) {
		valorVenda = Math.round((soma - (soma * (venda.desconto / 100)))*100)/100;
	}
	else {
		valorVenda = soma;
	}
	assert venda.total == valorVenda;
}

